// to generate a pick randomly, I used the Math.random() method to randomly generate numbers to target a string on the array. But since it only generates decimal numbers, in order to generate a whole number, I added Math.floor() method to round off the decimal.

function computerPlay() {
    const pick = ['rock', 'paper', 'scissors'];
    return pick[Math.floor(Math.random() * pick.length)];
}


// for the function playRound we added to paramters playerSelection(generated by the inout of the player) & computerSelection(is generated by the computerPlay() function we had created above).  Added toLowerCase() method to make the value inputted by the player is case insensitive.

function playRound(playerSelection, computerSelection) {
    if (playerSelection === "rock" && computerSelection === "rock" || playerSelection === "paper" && computerSelection === "paper" || playerSelection === "scissors" && computerSelection === "scissors") {
        alert(`It's a tie! You both pick ${playerSelection}`);
    } else if (playerSelection !== "rock" && playerSelection !== "paper" && playerSelection !== "scissors") {
        alert("You sure using that to play 'Rock, Paper, Scissors'?");    
    } else if (playerSelection === "rock" && computerSelection === "paper") {
        alert(`You lose! ${computerSelection} beats ${playerSelection}`);
    } else if (playerSelection === "paper" && computerSelection === "scissors") {
        alert(`You lose! ${computerSelection} beats ${playerSelection}`);
    } else if (playerSelection === "scissors" && computerSelection === "rock") {
        alert(`You lose! ${computerSelection} beats ${playerSelection}`);
    } else {
     return alert(`You win! ${playerSelection} beats ${computerSelection}`);
    } 
}

